mapper.update:
UPDATE JYL_MBOARD SET
TITLE=#{title}, CONTENT=#{content},WDATE=SYSDATE
WHERE SEQ=#{seq}

mapper.select:
SELECT SEQ ,ID ,TITLE,CONTENT, WDATE,REF,STEP,DEPTH,PARENT,DELFLAG FROM ( SELECT ROW_NUMBER() OVER ( ORDER BY REF ASC, STEP ASC) as rnum, SEQ ,ID ,TITLE,CONTENT, WDATE,REF,STEP,DEPTH,PARENT,DELFLAG FROM JYL_MBOARD WHERE 1 = 1
AND TITLE like '%'||#{s_keyword}||'%' AND CONTENT like '%'||#{s_keyword}||'%'
) A WHERE rnum between #{start} AND #{end}
